mapper.select:
select id as accessId,access_type,login_type,url,access_tips,remark
from database_access_url
where s_id = #{sid}
and d_id = #{did}

select *
from database_access_url
where s_id = #{sid}
and d_id = #{did}
and id = #{accessId}

mapper.delete:
delete from database_access_url
where s_id = #{sid}
and d_id = #{did}
and id = #{accessId}
