mapper.select:
select count(d_id) database_count,sum(price) money
from database_buy_plan_rel
where s_id = #{sid}
and plan_id = #{planId}

select id,d_id,price,remark
from database_buy_plan_rel
where s_id = #{sid}
and plan_id = #{planId}

    and d_id = #{did}

order by price desc

select count(d_id)
from database_buy_plan_rel
where s_id = #{sid}
and d_id = #{did}
and plan_id = #{planId}

    and id != #{planRelId}

select *
from database_buy_plan_rel
where s_id = #{sid}
and id = #{planRelId}

mapper.delete:
delete from database_buy_plan_rel
where s_id = #{sid}
and id = #{planRelId}

delete from database_buy_plan_rel
where s_id = #{sid}
and plan_id = #{planId}
