mapper.delete:
delete from database_contact_people
where s_id = #{sid}
and d_id = #{did}
and type = #{contactType}
and id = #{contactId}

mapper.select:
select *
 from database_contact_people
 where s_id = #{sid}
 and d_id = #{did}
 and type = #{type}

select *
from database_contact_people
where s_id = #{sid}
and d_id = #{did}

    and type = #{type}

and id = #{contactId}

select count(phone)
from database_contact_people
where s_id = #{sid}
and d_id = #{did}
and type = #{contactType}
and phone = #{phone}
