mapper.select:
select de.id, de.s_id, de.d_id, de.vyear, de.order_type, de.result_type,
dbi.data_time, dbi.company_id, dbi.agent_id, dbi.fulltext_flag
from database_evaluation de
left join database_base_info dbi on de.d_id = dbi.d_id and de.s_id = dbi.s_id
where de.s_id = #{sid} and de.vyear = #{vYear}

    and de.d_id = #{did}


    and dbi.fulltext_flag = #{fulltextFlag}


    and de.result_type = #{resultType}

select count(1)
from database_evaluation
where s_id = #{sid} and d_id = #{did} and vyear = #{vYear}

select id
from database_evaluation
where s_id = #{sid} and d_id = #{did} and vyear = #{vYear}

select id, s_id, d_id, vyear, order_type, time, result_type, remark
from database_evaluation
where s_id = #{sid} and vyear = #{vYear}

select distinct d_id
from database_evaluation
where s_id = #{sid} and vyear = #{vYear}

select order_type
from database_evaluation
where s_id = #{sid} and d_id = #{did} and vyear = #{vYear}
