mapper.select:
select id,d_id,vyear,stop_day,remark
from database_stop_info
where s_id = #{sid} and vyear = #{vYear}

    and d_id = #{did}

order by stop_day desc

select count(d_id)
from database_stop_info
where s_id = #{sid} and vyear = #{vYear} and d_id = #{did}

select *
from database_stop_info
where s_id = #{sid} and id = #{databaseStopId}

mapper.delete:
delete from
database_stop_info
where s_id = #{sid} and id = #{databaseStopId}
