mapper.select:
select distinct subject_id
from school_database_subject_rel
where s_id = #{sid} and d_id = #{did}

select distinct d_id
from school_database_subject_rel
where s_id = #{sid} and subject_id = #{subjectId}

select d_id id, subject_id value
from school_database_subject_rel
where s_id = #{sid}
