mapper.select:
SELECT * FROM school_database_list
WHERE s_id = #{sid}
  AND d_id = #{did}
  AND vyear = #{vYear}

select distinct d_id
from school_database_list
where s_id = #{sid}

    and vyear = #{vYear}

and order_type != 0 and order_type != 5

mapper.delete:
delete from
school_database_list
where s_id = #{sid} and d_id = #{did} and vyear = #{vYear}
