mapper.select:
SELECT count(1)
FROM user
WHERE role_id = #{roleId}

select u.id,u.account_name,u.nick_name,u.role_id,r.name as role,u.disable_flag,u.department,u.email
from user as u
left join role as r
on u.role_id = r.id
where u.s_id = #{sid}

    and u.nick_name like concat('%',#{nickName},'%')


    and u.role_id = #{roleId}


    and u.disable_flag = #{disableFlag}

select count(id) from user
where
s_id = #{sid}
and account_name = #{accountName}

SELECT id, nick_name, email
FROM user
WHERE s_id = #{sid} AND disable_flag = #{disableFlag}

SELECT *
FROM user
WHERE s_id = #{sid} AND id = #{userId}
